BTLanalysisBase <- if (requireNamespace('jmvcore')) R6::R6Class(
    "BTLanalysisBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = 'DPACanalyses',
                name = 'BTLanalysis',
                version = c(1,0,0),
                options = options,
                results = BTLanalysisResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE)
        }))

#' Bradley-Terry-Luce model and statistics
#'
#' 
#' @param data .
#' @param Repr1 .
#' @param Repr2 .
#' @param Selected .
#' @param Judge .
#' @param estIters .
#' @param epsCor .
#' @param rel .
#' @param plotGraph .
#' @param plotScale .
#' @param misfit .
#' @param flagBound .
#' @param misfitPlot .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$debugText} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$mainTitle$rel} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$mainTitle$tableTitle} \tab \tab \tab \tab \tab a group \cr
#'   \code{results$mainTitle$networkPlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$mainTitle$scalePlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$mainTitle$misfitTable} \tab \tab \tab \tab \tab a group \cr
#' }
#'
#' @export
BTLanalysis <- function(
    data,
    Repr1,
    Repr2,
    Selected,
    Judge,
    estIters = 4,
    epsCor = 0.003,
    rel = FALSE,
    plotGraph = FALSE,
    plotScale = FALSE,
    misfit,
    flagBound = 2,
    misfitPlot = FALSE) {

    if ( ! requireNamespace('jmvcore'))
        stop('BTLanalysis requires jmvcore to be installed (restart may be required)')

    options <- BTLanalysisOptions$new(
        Repr1 = Repr1,
        Repr2 = Repr2,
        Selected = Selected,
        Judge = Judge,
        estIters = estIters,
        epsCor = epsCor,
        rel = rel,
        plotGraph = plotGraph,
        plotScale = plotScale,
        misfit = misfit,
        flagBound = flagBound,
        misfitPlot = misfitPlot)

    results <- BTLanalysisResults$new(
        options = options)

    analysis <- BTLanalysisClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
